mapper.select:
SELECT floor(currentPage/blockCount) blockPage, blockCount, 1+(floor(currentPage/blockCount)*blockCount) startPageNum, case when 5+(floor(currentPage/blockCount)*blockCount) > totalPage then totalPage else 5+(floor(currentPage/blockCount)*blockCount) end lastPageNum, totalCount, totalPage, currentPage, CASE WHEN currentPage = 0 THEN 1 ELSE 0 END isFirst, CASE WHEN currentPage = totalPage-1 THEN 1 ELSE 0 END isLast FROM ( SELECT 0 blockPage, 5 blockCount, 0 startPageNum, 0 lastPageNum, count(*) totalCount, ceil(count(*)/3) totalPage, #{page} currentPage, 0 isFirst, 0 isLast FROM boards
WHERE title LIKE '%'||#{keyword}||'%'
) boards
